(function() {
  // JS is only used to get the current time.
  // The needles are rotated when the page loads, the CSS does the rest to animate.
  var ct, cth, ctm, d, h, m, s, setTime;

  d = new Date();

  h = d.getHours();

  m = d.getMinutes();

  s = d.getSeconds();

  ct = function(v) {
    var t;
    t = v * 360 / 60;
    return t;
  };

  ctm = function(v) {
    var t;
    t = v * 360 / 60 + 6 * ct(s) / 360; // 1min = 6 deg
    return t;
  };

  cth = function(v) {
    var t;
    t = v * 360 / 12 + 30 * ct(m) / 360; // 1h = 30 deg
    return t;
  };

  setTime = function() {
    $('.w-sec').css('transform', 'rotateZ(' + ct(s) + 'deg)');
    $('.w-min').css('transform', 'rotateZ(' + ctm(m) + 'deg)');
    return $('.w-hour').css('transform', 'rotateZ(' + cth(h) + 'deg)');
  };

  $(document).ready(function() {
    return setTime();
  });

}).call(this);

